package com.scritian.googlecourse;

import android.support.v7.app.AppCompatActivity;
import android.os.Bundle;
import android.support.v7.widget.LinearLayoutManager;
import android.support.v7.widget.RecyclerView;

import java.util.ArrayList;

public class MoviesActivity extends AppCompatActivity {

    private RecyclerView mRecyclerView;
    private RecyclerView.Adapter mAdapter;
    private RecyclerView.LayoutManager mLayoutManager;

    private ArrayList<Movie> moviesList;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_movies);

        mRecyclerView = (RecyclerView) findViewById(R.id.rv_movies);

        // use this setting to improve performance if you know that changes
        // in content do not change the layout size of the RecyclerView
        mRecyclerView.setHasFixedSize(true);

        // use a linear layout manager
        mLayoutManager = new LinearLayoutManager(this);
        mRecyclerView.setLayoutManager(mLayoutManager);

        moviesList = new ArrayList<>();
        moviesList.add(new Movie("Interstellar","description","Sci-Fi",4.7f,"INTERSTELLAR", null));
        moviesList.add(new Movie("The Departed", "descripton", "Crime", 4.3f, "DEPARTED", null));
        moviesList.add(new Movie("Inception", "description", "Sci-Fi", 4.0f, "INCEPTION", null));
        moviesList.add(new Movie("Spirited Away", "description", "Animated, Fantasy", 4.7f, "SPIRITED_AWAY", null));
        moviesList.add(new Movie("Avengers Infinite", "description", "Sci-Fi, Fantasy", 5.0f, "", null));

        // specify an adapter (see also next example)
        mAdapter = new MoviesAdapter(moviesList);
        mRecyclerView.setAdapter(mAdapter);
    }
}
